INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES
(-1615042, '%s begins to open a Twilight Portal!', NULL, NULL, '%s beginnt ein Zwielichtportal zu öffnen', NULL, NULL, NULL, NULL, '%s начинает открывать Врата Сумерек!', 0, 5, 0, 0, 'sartharion drake WHISPER_OPEN_PORTAL'),
(-1615041, 'A Vesperon Disciple appears in the Twilight!', NULL, NULL, 'Ein Akolyt von Vesperon erscheint im Zwielicht!', NULL, NULL, NULL, NULL, 'Ученик Весперон появляется в Сумерках!', 0, 5, 0, 0, 'shadron WHISPER_VESPERON_DICIPLE'),
(-1615040, 'Unlike, I have many talents.', NULL, NULL, 'Im Gegensatz zu euch habe ich viele Talents', NULL, NULL, NULL, NULL, 'В отличии от вас, я кое что умею.', 14138, 1, 0, 0, 'vesperon SAY_VESPERON_SPECIAL_2'),
(-1615039, 'Aren''t you tricky...I have a few tricks of my own...', NULL, NULL, 'Netter Trick... Aber auch ich habe die eine oder andere Überraschung auf Lager!', NULL, NULL, NULL, NULL, 'С вами не просто... Но и со мной не легко...', 14137, 1, 0, 0, 'vesperon SAY_VESPERON_SPECIAL_1'),
(-1615038, 'Father was right about you, Sartharion...You are a weakling!', NULL, NULL, 'Vater hatte Recht was euch anbelangt, Sartharion... Ihr seid ein Schwächling!', NULL, NULL, NULL, NULL, 'Отец был прав насчет тебя, Сртарион... Ты слаб!', 14139, 1, 0, 0, 'vesperon SAY_VESPERON_RESPOND'),
(-1615037, 'I will pick my teeth with your bones!', NULL, NULL, 'Ich werde eure Knochen als Zahnstocher benutzen!', NULL, NULL, NULL, NULL, 'Я буду ковыряться в зубах вашими костями!', 14136, 1, 0, 0, 'vesperon SAY_VESPERON_BREATH'),
(-1615036, 'I still have some...fight..in...me...', NULL, NULL, 'Ich kann...noch immer...kämpfen...', NULL, NULL, NULL, NULL, 'Я все еще... могу... драться...', 14140, 1, 0, 0, 'vesperon SAY_VESPERON_DEATH'),
(-1615035, 'Was that the best you can do?', NULL, NULL, 'War das schon alles, was ihr könnt?', NULL, NULL, NULL, NULL, 'И это все, что ты можешь сделать?', 14135, 1, 0, 0, 'vesperon SAY_VESPERON_SLAY_2'),
(-1615034, 'The least you could do is put up a fight...', NULL, NULL, 'Ihr hättet mir wenigstens einen anständigen Kampf liefern können...', NULL, NULL, NULL, NULL, 'Все, что тебе удалось, это затеять драку...', 14134, 1, 0, 0, 'vesperon SAY_VESPERON_SLAY_1'),
(-1615033, 'You pose no threat, lesser beings...give me your worst!', NULL, NULL, 'Ihr stellt keine Bedrohung dar, niedere Wesen...zeigt mir, was in euch steckt!', NULL, NULL, NULL, NULL, 'Вы безобидны, ничтожные создания... покажите мне, на что вы способны!', 14133, 1, 0, 0, 'vesperon SAY_VESPERON_AGGRO'),
(-1615032, 'The lava surrounding %s churns!', NULL, NULL, 'Lava umgibt %s peitschend!', NULL, NULL, NULL, NULL, 'Потомство Весперона появляется в Сумерках!', 0, 5, 0, 0, 'sartharion WHISPER_LAVA_CHURN'),
(-1615031, 'This is why we call you lesser beeings.', NULL, NULL, 'Aus diesem Grund nennen wir euch niedere Wesen', NULL, NULL, NULL, NULL, 'Вот поэтому мы и называем вас низшими созданиями.', 14097, 1, 0, 0, 'sartharion SAY_SARTHARION_SLAY_3'),
(-1615030, 'You are the grave disadvantage.', NULL, NULL, 'Ihr seit in einer todtraurigen Lage.', NULL, NULL, NULL, NULL, 'Тебе смертельно не повезло.', 14096, 1, 0, 0, 'sartharion SAY_SARTHARION_SLAY_2'),
(-1615029, 'You will make a fine meal for the hatchlings.', NULL, NULL, 'Ihr werdet ein köstliches Mahl für die Brut abgeben.', NULL, NULL, NULL, NULL, 'Из тебя выйдет хорошая закуска для детенышей.', 14094, 1, 0, 0, 'sartharion SAY_SARTHARION_SLAY_1'),
(-1615028, 'All will be reduced to ash!', NULL, NULL, 'Nur noch Asche wird übrig bleiben!', NULL, NULL, NULL, NULL, 'От вас останется всего лишь горстка пепла!', 14102, 1, 0, 0, 'sartharion SAY_SARTHARION_SPECIAL_4'),
(-1615027, 'How much heat can you take?', NULL, NULL, 'Wieviel Hitze könnt ihr vertragen?', NULL, NULL, NULL, NULL, 'Посмотрим, как вы переносите жару!', 14101, 1, 0, 0, 'sartharion SAY_SARTHARION_SPECIAL_3'),
(-1615026, 'Your charred bones will litter the floor!', NULL, NULL, 'Nur mehr eure verkohlten Knochen werden den Boden bedecken!', NULL, NULL, NULL, NULL, 'Ваши обугленные кости завалят весь пол!', 14100, 1, 0, 0, 'sartharion SAY_SARTHARION_SPECIAL_2'),
(-1615025, 'Such flammable little insects....', NULL, NULL, 'Wie leicht sie brennen die kleinen Maden...', NULL, NULL, NULL, NULL, 'Мелкая мошка хорошо горит...', 14099, 1, 0, 0, 'sartharion SAY_SARTHARION_SPECIAL_1'),
(-1615024, 'Such is the price... of failure...', NULL, NULL, 'Dies ist der Preise...des Versagens...', NULL, NULL, NULL, NULL, 'Такова цена... неудачи...', 14107, 1, 0, 0, 'sartharion SAY_SARTHARION_DEATH'),
(-1615023, 'Vesperon! The clutch is in danger! Assist me!', NULL, NULL, 'Vesperon! Das Gelege ist in Gefahr! Helft mir!', NULL, NULL, NULL, NULL, 'Весперон! Кладка в опасности! Помоги мне!', 14104, 1, 0, 0, 'sartharion SAY_SARTHARION_CALL_VESPERON'),
(-1615022, 'Tenebron! The eggs are yours to protect as well!', NULL, NULL, 'Tenebron! Auch ihr sollt die Eier schützen!', NULL, NULL, NULL, NULL, 'Тенеброн! Ты тоже должна защищать яйца!', 14106, 1, 0, 0, 'sartharion SAY_SARTHARION_CALL_TENEBRON'),
(-1615021, 'Shadron! Come to me, all is at risk!', NULL, NULL, 'Shadron kommt zu mir! Der Sieg steht auf Messersschneide!', NULL, NULL, NULL, NULL, 'Шадрон! На помощь! Все на кону!', 14105, 1, 0, 0, 'sartharion SARTHARION_CALL_SHADRON'),
(-1615020, 'Burn, you miserable wretches!', NULL, NULL, 'Brennt ihr armseligen Wichte!', NULL, NULL, NULL, NULL, 'Гори, злосчастный сброд!', 14098, 1, 0, 0, 'sartharion SAY_SARTHARION_BREATH'),
(-1615019, 'This pathetic siege ends NOW!', NULL, NULL, 'Dieser lächerliche Angriff endet JETZT!', NULL, NULL, NULL, NULL, 'Это смехотворное сражение закончиться СЕЙЧАС ЖЕ!', 14103, 1, 0, 0, 'sartharion SAY_SARTHARION_BERSERK'),
(-1615018, 'It is my charge to watch over these eggs. I will see you burn before any harm comes to them!', NULL, NULL, 'Meine Aufgabe ist es über diese Eier zu wachen. Kommt ihnen zu nahe und von euch bleibt nur ein Häuflein Asche!', NULL, NULL, NULL, NULL, 'Моя обязанность - оберегать эти яйца. И вы сгорите прежде, чем хоть пальцем дотронетесь их.', 14093, 1, 0, 0, 'sartharion SAY_SARTHARION_AGGRO'),
(-1615017, '%s begins to hatch eggs in the twilight!', NULL, NULL, '%s beginnt Eier im Zwielicht auszuschlüpfen zu lassen!', NULL, NULL, NULL, NULL, '%s начинает высиживать яйца в сумерках!', 0, 5, 0, 0, 'tenebron WHISPER_HATCH_EGGS'),
(-1615016, 'I am no mere dragon! You will find I am much, much, more...', NULL, NULL, 'Ich bin nicht nur ein einfacher Drache! Ihr werdet sehn ich bin viel, viel mehr', NULL, NULL, NULL, NULL, 'Я не простой дракон! Увидите, совсем, совсем, не простой...', 14127, 1, 0, 0, 'tenebron SAY_TENEBRON_SPECIAL_2'),
(-1615015, 'Arrogant little creatures! To challenge powers you do not yet understand...', NULL, NULL, 'Arrogante kleine Kreaturen! Euch ist noch nicht einmal klar,was für Mächte ihr da herausfordert...', NULL, NULL, NULL, NULL, 'Самонадеянные мелкие твари! Вы бросаете вызов силам, от понимания которых, вы еще далеки!', 14126, 1, 0, 0, 'tenebron SAY_TENEBRON_SPECIAL_1'),
(-1615014, 'It is amusing to watch you struggle. Very well, witness how it is done.', NULL, NULL, 'Amüsant zuzusehen wie ihr euch abmüht. Nun gut lasst mich euch zeigen, wie es gemacht wird.', NULL, NULL, NULL, NULL, 'Занятно наблюдать, как ты сражаешься. Хорошо, смотри, как надо это делать.', 14128, 1, 0, 0, 'tenebron SAY_TENEBRON_RESPOND'),
(-1615013, 'To darkness I condemn you...', NULL, NULL, 'Ich verdamme euch in die Dunkelheit...', NULL, NULL, NULL, NULL, 'Приговариваю вас к тьме...', 14125, 1, 0, 0, 'tenebron SAY_TENEBRON_BREATH'),
(-1615012, 'I should not... have held back...', NULL, NULL, 'Ich hätte mich nicht... zurückhalten sollen...', NULL, NULL, NULL, NULL, 'Не надо было... вмешиваться...', 14129, 1, 0, 0, 'tenebron SAY_TENEBRON_DEATH'),
(-1615011, 'Typical... Just as I was having fun.', NULL, NULL, 'Typisch...Gerade als es anfing Spaß zu machen.', NULL, NULL, NULL, NULL, 'Как обычно... Только я вошла во вкус.', 14124, 1, 0, 0, 'tenebron SAY_TENEBRON_SLAY_2'),
(-1615010, 'No contest.', NULL, NULL, 'Keine Herausforderung', NULL, NULL, NULL, NULL, 'Не интересно.', 14123, 1, 0, 0, 'tenebron SAY_TENEBRON_SLAY_1'),
(-1615009, 'You have no place here. Your place is among the departed.', NULL, NULL, 'Ihr gehört hier nicht her. Euer Platz ist bei den Gefallenen', NULL, NULL, NULL, NULL, 'Вам здесь не место. Ваше место - среди усопших.', 14122, 1, 0, 0, 'tenebron SAY_TENEBRON_AGGRO'),
(-1615008, 'A Shadron Disciple appears in the Twilight!', NULL, NULL, 'Ein Akolyt von Shadron erscheint im Zwielicht!', NULL, NULL, NULL, NULL, 'Ученик Шадрон появляется в Сумерках!', 0, 5, 0, 0, 'shadron WHISPER_SHADRON_DICIPLE'),
(-1615007, 'On your knees!', NULL, NULL, 'Auf die Knie!', NULL, NULL, NULL, NULL, 'На колени!', 14116, 1, 0, 0, 'shadron SAY_SHADRON_SPECIAL_2'),
(-1615006, 'Father tought me well!', NULL, NULL, 'Vater hat uns einiges gelehrt!', NULL, NULL, NULL, NULL, 'Отец не зря учил меня!', 14115, 1, 0, 0, 'shadron SAY_SHADRON_SPECIAL_1'),
(-1615005, 'I will take pity on you Sartharion, just this once.', NULL, NULL, 'Ich werde Erbarmen mit euch haben Sartharion, aber nur dies eine Mal.', NULL, NULL, NULL, NULL, 'Я пожалею тебя Сартарион, но только в этот раз.', 14117, 1, 0, 0, 'shadron SAY_SHADRON_RESPOND'),
(-1615004, 'You are easily bested! ', NULL, NULL, 'Ihr seit so einfach zu besiegen!', NULL, NULL, NULL, NULL, 'Вас легко провести!', 14114, 1, 0, 0, 'shadron SAY_SHADRON_BREATH'),
(-1615003, 'We...are superior! How could this...be...', NULL, NULL, 'Wir...sind überlegen! Wie konnte das...geschehen...', NULL, NULL, NULL, NULL, 'Мы... выше всех! Как это... возможно...', 14118, 1, 0, 0, 'shadron SAY_SHADRON_DEATH'),
(-1615002, 'Such mediocre resistance!', NULL, NULL, 'Welch mittelmäßiger Wiederstand!', NULL, NULL, NULL, NULL, 'Какое слабое сопротивление!', 14113, 1, 0, 0, 'shadron SAY_SHADRON_SLAY_2'),
(-1615001, 'You are insignificant!', NULL, NULL, 'Ihr seit bedeutungslos!', NULL, NULL, NULL, NULL, 'Ты ничтожество!', 14112, 1, 0, 0, 'shadron SAY_SHADRON_SLAY_1'),
(-1615000, 'I fear nothing! Least of all you!', NULL, NULL, 'Ich fürchte nichts und niemanden! Am allerwenigsten euch!', NULL, NULL, NULL, NULL, 'Я не боюсь ничего! Тем более вас!', 14111, 1, 0, 0, 'shadron SAY_SHADRON_AGGRO');
